import React, { useState } from 'react';
import './App.css';

function App() {
  return (
    <div className="app">
      <div className="graph-background">
        {/* Future graph elements can go here */}
      </div>
      <DynamicButton />
    </div>
  );
}

function DynamicButton() {
  const [showInputPage, setShowInputPage] = useState(false);
  const [closing, setClosing] = useState(false);
  const [formData, setFormData] = useState({
    message: "",
    polynomial: "",
    codedMessage: "",
    errors: "",
    recievedMessage: "",
    decodedMessage: "",
    codeWord: "",
  });

  const handleOpen = () => {
    setShowInputPage(true);
    setClosing(false);
  };

  const handleClose = () => {
    setClosing(true);
    setTimeout(() => {
      setShowInputPage(false);
      setClosing(false);
    }, 500); 
  };

  return (
    <>
      <button
        className="dynamic-button"
        onClick={handleOpen}
      >
       Zadaj Parametre
      </button>
      {showInputPage && (
         <div className={`input-page ${closing ? 'slide-down' : 'slide-up'}`}>
          <div className="input-container">
            <div className="input-group">
              <label>Správa:</label>
              <input type="text" placeholder="10011" />
            </div>
            <div className="input-group">
              <label>Generujúci polynóm:</label>
              <input type="text" placeholder="111;101;" />
            </div>
            <div className="input-group">
              <label>Kódovaná správa:</label>
              <input type="text" placeholder="" />
            </div>
            <div className="input-group">
              <label>Chyby v prenose:</label>
              <input type="text" placeholder="" />
            </div>
            <div className="input-group">
              <label>Prijatá správa:</label>
              <input type="text" placeholder="" />
            </div>
            <div className="input-group">
              <label>Dekódovaná správa:</label>
              <input type="text" placeholder="" />
            </div>
            <div className="input-group">
              <label>Kódové slovo:</label>
              <input type="text" placeholder="" />
            </div>
          </div>
          <div className="button-container">
            <button onClick={() => alert('Kóduj')}>Kóduj</button>
            <button onClick={() => alert('Dekóduj')}>Dekóduj</button>
            <button onClick={handleClose}>Close</button>
          </div>
        </div>
      )}
    </>
  );
}
export default App;
