import React, { useEffect, useRef } from "react";
import * as d3 from "d3";

const TrellisGraph = ({ nodes, links }) => {
  const svgRef = useRef();

  useEffect(() => {
    const svg = d3.select(svgRef.current);
    const width = 800;
    const height = 400;

    svg.attr("width", width).attr("height", height);

    // Clear previous render
    svg.selectAll("*").remove();

    // Create a scale for positioning nodes
    const xScale = d3
      .scalePoint()
      .domain(nodes.map((node) => node.time))
      .range([0, width]);

    const yScale = d3
      .scalePoint()
      .domain(nodes.map((node) => node.state))
      .range([0, height]);

    // Draw links (transitions between states)
    svg
      .selectAll("line")
      .data(links)
      .join("line")
      .attr("x1", (d) => xScale(d.source.time))
      .attr("y1", (d) => yScale(d.source.state))
      .attr("x2", (d) => xScale(d.target.time))
      .attr("y2", (d) => yScale(d.target.state))
      .attr("stroke", "black")
      .attr("stroke-width", 1);

    // Draw nodes (states at each time step)
    svg
      .selectAll("circle")
      .data(nodes)
      .join("circle")
      .attr("cx", (d) => xScale(d.time))
      .attr("cy", (d) => yScale(d.state))
      .attr("r", 5)
      .attr("fill", "blue");

    // Add labels to nodes
    svg
      .selectAll("text")
      .data(nodes)
      .join("text")
      .attr("x", (d) => xScale(d.time))
      .attr("y", (d) => yScale(d.state) - 10)
      .attr("text-anchor", "middle")
      .attr("font-size", "10px")
      .text((d) => `S${d.state}`);
  }, [nodes, links]);

  return <svg ref={svgRef}></svg>;
};

export default TrellisGraph;
