import React, { useState } from 'react';
import './App.css';

function App() {

  const [response, setResponse] = useState(""); 
  const [codedMessage, setCodedMessage] = useState(""); 
  const [channel, setChannel] = useState(""); 
  const [decodedMessage, setDecodedMessage] = useState(""); 

  return (
    <div className="app">
      <GraphBackground response={response} />
      <DynamicButton 
      setResponse={setResponse} 
      codedMessage={codedMessage} 
      setCodedMessage={setCodedMessage} 
      channel={channel} 
      setChannel={setChannel} 
      decodedMessage={decodedMessage} 
      setDecodedMessage={setDecodedMessage}/>
    </div>
  );
}

function GraphBackground({ response }) {
  return (
    <div className="graph-background">
      {/* Display response message */}
      {response && <div className="response-message">{response}</div>}
    </div>
  );
}

function DynamicButton({ setResponse, codedMessage, setCodedMessage, channel, setChannel, decodedMessage, setDecodedMessage }) {
  const [showInputPage, setShowInputPage] = useState(false);
  const [closing, setClosing] = useState(false);
  const [requestFormData, setRequestFormData] = useState({
    message: "",
    polynomial: "",
    codedMessage: "",
    errors: "",
    recievedMessage: "",
    decodedMessage: "",
    codeWord: "",
  });

  const handleOpen = () => {
    setShowInputPage(true);
    setClosing(false);
  };

  const handleClose = () => {
    setClosing(true);
    setTimeout(() => {
      setShowInputPage(false);
      setClosing(false);
    }, 500); 
  };

  //Input Field Handler
  const handleChange = (e) => {
    const { name, value } = e.target;
    setRequestFormData(prevData => ({ ...prevData, [name]: value }));
  };

  //Text Field Handler
  const handleTextChange = (e) => {
    const { name, value } = e.target;
    if (name === "codedMessage") {
      setCodedMessage(value);
    } else if (name === "channel") {
      setChannel(value);
    } else if (name === "decodedMessage") {
      setDecodedMessage(value);
  }
  };

  const handleCode = async () => {
    try {
      const response = await fetch("http://localhost:8080/api/code", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(requestFormData)
      });

      const result = await response.json();
      console.log("Response from backend:", result);
      setResponse(result.codedMessage + " " + result.channel|| "No response message");
      setCodedMessage(result.codedMessage || "No coded message");
      setChannel(result.channel || "No channel");
    } catch (error) {
      console.error("Error submitting form data:", error);
    }
  };

  const handleDecode = async () => {
    try {
      const response = await fetch("http://localhost:8080/api/decode", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(requestFormData)
      });

      const result = await response.json();
      console.log("Response from backend:", result);
      setResponse(result.decodedMessage || "No response message");
      setChannel(result.decodedMessage || "No channel");
    } catch (error) {
      console.error("Error submitting form data:", error);
    }
  };

  return (
    <>
      <button
        className="dynamic-button"
        onClick={handleOpen}
      >
       Parametre
      </button>
      {showInputPage && (
         <div className={`input-page ${closing ? 'slide-down' : 'slide-up'}`}>
          <div className="input-container">
            <div className="input-group">
              <label>Správa:</label>
              <input type="text" name="message" placeholder="10011" value={requestFormData.message} onChange={handleChange}/>
            </div>
            <div className="input-group">
              <label>Generujúci polynóm:</label>
              <input type="text" name="polynomial" placeholder="111;101;" value={requestFormData.polynomial} onChange={handleChange}/>
            </div>
            <div className="input-group">
              <label>Kódovaná správa:</label>
              <textarea  type="text" name="codedMessage" readOnly className="non-resizable-textarea" value={codedMessage} onChange={handleTextChange}/>
            </div>
            <div className="input-group">
              <label>Chyby v prenose:</label>
              <textarea  type="text" name="channel" readOnly className="non-resizable-textarea" value={channel} onChange={handleTextChange}/>
            </div>
            <div className="input-group">
              <label>Prijatá správa:</label>
              <textarea type="text" name="recievedMessage" readOnly className="non-resizable-textarea" value={requestFormData.decodedMessage} onChange={handleTextChange}/>
            </div>
            <div className="input-group">
              <label>Dekódovaná správa:</label>
              <input type="text" name="decodedMessage" placeholder="" value={requestFormData.decodedMessage} onChange={handleChange}/>
            </div>
            <div className="input-group">
              <label>Kódové slovo:</label>
              <input type="text" name="codeWord" placeholder="" value={requestFormData.codeWord} onChange={handleChange}/>
            </div>
          </div>
          <div className="button-container">
            <button onClick={handleCode}>Kóduj</button>
            <button onClick={handleDecode}>Dekóduj</button>
            <button onClick={handleClose}>Close</button>
          </div>
        </div>
      )}
    </>
  );
}
export default App;
