import React, { useState } from 'react';
import './App.css';

function App() {
  return (
    <div className="app">
      <div className="graph-background">
        {/* Future graph elements can go here */}
      </div>
      <DynamicButton />
    </div>
  );
}

function DynamicButton() {
  const [showInputPage, setShowInputPage] = useState(false);

  return (
    <>
      <button
        className="dynamic-button"
        onClick={() => setShowInputPage(true)}
      >
        Open Input Page
      </button>
      {showInputPage && (
        <div className="input-page slide-up">
          <div className="input-container">
            {/* Input fields with labels */}
            {Array.from({ length: 8 }, (_, index) => (
              <div className="input-group" key={index}>
                <label>{`Label ${index + 1}`}</label>
                <input type="text" placeholder={`Input ${index + 1}`} />
              </div>
            ))}
          </div>
          <div className="button-container">
            <button onClick={() => setShowInputPage(false)}>Close</button>
            <button onClick={() => alert('Submit clicked')}>Submit</button>
          </div>
        </div>
      )}
    </>
  );
}
export default App;
