import React, { useState } from 'react';
import './App.css';

function App() {
  return (
    <div className="app">
      <div className="graph-background">
        {/* Future graph elements can go here */}
      </div>
      <DynamicButton />
    </div>
  );
}

function DynamicButton() {
  const [showInputPage, setShowInputPage] = useState(false);
  const [closing, setClosing] = useState(false);
  const [requestFormData, setRequestFormData] = useState({
    message: "",
    polynomial: "",
    codedMessage: "",
    errors: "",
    recievedMessage: "",
    decodedMessage: "",
    codeWord: "",
  });

  const [response, setResponse] = useState("");

  const handleOpen = () => {
    setShowInputPage(true);
    setClosing(false);
  };

  const handleClose = () => {
    setClosing(true);
    setTimeout(() => {
      setShowInputPage(false);
      setClosing(false);
    }, 500); 
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = async () => {
    try {
      const response = await fetch("http://localhost:8080/api/submit", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(requestFormData)
      });

      const result = await response.json();
      setResponse(result.responseMessage || "No response message");
    } catch (error) {
      console.error("Error submitting form data:", error);
    }
  };

  return (
    <>
      <button
        className="dynamic-button"
        onClick={handleOpen}
      >
       Zadaj Parametre
      </button>
      {showInputPage && (
         <div className={`input-page ${closing ? 'slide-down' : 'slide-up'}`}>
          <div className="input-container">
            <div className="input-group">
              <label>Správa:</label>
              <input type="text" name="message" placeholder="10011" value={requestFormData.message} onChange={handleChange}/>
            </div>
            <div className="input-group">
              <label>Generujúci polynóm:</label>
              <input type="text" name="polynomial" placeholder="111;101;" value={requestFormData.polynomial} onChange={handleChange}/>
            </div>
            <div className="input-group">
              <label>Kódovaná správa:</label>
              <input type="text" name="codedMessage" placeholder="" value={requestFormData.codedMessage} onChange={handleChange}/>
            </div>
            <div className="input-group">
              <label>Chyby v prenose:</label>
              <input type="text" name="errors" placeholder="" value={requestFormData.errors} onChange={handleChange}/>
            </div>
            <div className="input-group">
              <label>Prijatá správa:</label>
              <input type="text" name="recievedMessage" placeholder="" value={requestFormData.recievedMessage} onChange={handleChange}/>
            </div>
            <div className="input-group">
              <label>Dekódovaná správa:</label>
              <input type="text" name="decodedMessage" placeholder="" value={requestFormData.decodedMessage} onChange={handleChange}/>
            </div>
            <div className="input-group">
              <label>Kódové slovo:</label>
              <input type="text" name="codeWord" placeholder="" value={requestFormData.codeWord} onChange={handleChange}/>
            </div>
          </div>
          <div className="button-container">
            <button onClick={handleSubmit}>Kóduj</button>
            <button onClick={() => alert('Dekóduj')}>Dekóduj</button>
            <button onClick={handleClose}>Close</button>
          </div>
          {response && <div className="response-message">{response}</div>}
        </div>
      )}
    </>
  );
}
export default App;
